import * as i0 from "@angular/core";
import * as i1 from "@angular/router";
import * as i2 from "@angular/common";
export class JitsiComponent {
    constructor(router) {
        this.router = router;
        this.domain = "meet.jit.si"; // For self hosted use your domain
        // For Custom Controls
        this.isAudioMuted = false;
        this.isVideoMuted = false;
        this.handleClose = () => {
            console.log("handleClose");
        };
        this.handleParticipantLeft = async (participant) => {
            console.log("handleParticipantLeft", participant); // { id: "2baa184e" }
            const data = await this.getParticipants();
        };
        this.handleParticipantJoined = async (participant) => {
            console.log("handleParticipantJoined", participant); // { id: "2baa184e", displayName: "Shanu Verma", formattedDisplayName: "Shanu Verma" }
            const data = await this.getParticipants();
        };
        this.handleVideoConferenceJoined = async (participant) => {
            console.log("handleVideoConferenceJoined", participant); // { roomName: "bwb-bfqi-vmh", id: "8c35a951", displayName: "Akash Verma", formattedDisplayName: "Akash Verma (me)"}
            const data = await this.getParticipants();
        };
        this.handleVideoConferenceLeft = () => {
            console.log("handleVideoConferenceLeft");
            this.router.navigate(['/thank-you']);
        };
        this.handleMuteStatus = (audio) => {
            console.log("handleMuteStatus", audio); // { muted: true }
        };
        this.handleVideoStatus = (video) => {
            console.log("handleVideoStatus", video); // { muted: true }
        };
    }
    ngOnInit() {
        this.room = 'bwb-bfqi-vmh'; // Set your room name
        this.user = {
            name: 'Akash Verma' // Set your username
        };
    }
    ngAfterViewInit() {
        this.options = {
            roomName: this.room,
            width: 900,
            height: 500,
            configOverwrite: { prejoinPageEnabled: false },
            interfaceConfigOverwrite: {
            // overwrite interface properties
            },
            parentNode: document.querySelector('#jitsi-iframe'),
            userInfo: {
                displayName: this.user.name
            }
        };
        this.api = new JitsiMeetExternalAPI(this.domain, this.options);
        // Event handlers
        this.api.addEventListeners({
            readyToClose: this.handleClose,
            participantLeft: this.handleParticipantLeft,
            participantJoined: this.handleParticipantJoined,
            videoConferenceJoined: this.handleVideoConferenceJoined,
            videoConferenceLeft: this.handleVideoConferenceLeft,
            audioMuteStatusChanged: this.handleMuteStatus,
            videoMuteStatusChanged: this.handleVideoStatus
        });
    }
    getParticipants() {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                resolve(this.api.getParticipantsInfo()); // get all participants
            }, 500);
        });
    }
}
